import React, { useEffect, useRef, useState } from "react";
import AccountCircleIcon from "@material-ui/icons/AccountCircle";
import SendIcon from "@material-ui/icons/Send";
import Users from "./Users";
import { connect } from "react-redux";
import io from "socket.io-client";
import { useParams } from "react-router";
const socket = io("http://localhost:7000");
function Chat(props) {
  if (props.isloggedin != true) {
    props.history.push("/");
  }
  console.log("chatssss", props);
  const params = useParams();
  const [message, setMessage] = useState("");
  const [chat, setChat] = useState([]);
  const [people, setPeople] = useState([]);
  console.log("chatttttsssss", chat);
  useEffect(() => {
    socket.on("new-client", (user) => {
      console.log("new online user found.....", user);
      setPeople(user);
    });

    socket.on("message", (payload) => {
      setChat([...chat, payload]);
    });
  });

  const sendMessage = (e) => {
    e.preventDefault();
    if (props.custom) {
      console.log("parmas", params.id);
      console.log(message);
      const msgData = {
        msg: message,
        id: props.user.id,
        time: new Date(),
        to: params.id,
      };
      socket.emit("custom-message", msgData);
    } else {
      console.log(message);
      const msgData = {
        msg: message,
        id: props.user.id,
        time: new Date(),
      };
      socket.emit("message", msgData);
    }

    setMessage("");
  };

  return (
    <>
      <div className="container">
        <h3 className=" text-center">Welcome {props?.user}</h3>
        <div className="messaging">
          <div className="inbox_msg">
            <Users people={people} />
            <div className="mesgs">
              <h4> {"Public"}</h4>
              <div className="msg_history">
                {chat.map((msg) => {
                  if (msg.id === props.user.id) {
                    return (
                      <div className="outgoing_msg" key={msg.id}>
                        <div className="sent_msg">
                          <p>{msg.msg}</p>
                          <span className="time_date"> {msg.time}</span>{" "}
                        </div>
                      </div>
                    );
                  } else {
                    return (
                      <div className="incoming_msg" key={msg.id}>
                        <div className="incoming_msg_img">
                          <AccountCircleIcon />
                        </div>
                        <div className="received_msg">
                          <div className="received_withd_msg">
                            <p> {msg.msg}</p>
                            <span className="time_date">{msg.time}</span>
                          </div>
                        </div>
                      </div>
                    );
                  }
                })}
              </div>
              <div className="type_msg">
                <div className="input_msg_write">
                  <form onSubmit={sendMessage}>
                    <input
                      type="text"
                      name="message"
                      placeholder="Type message"
                      value={message}
                      onChange={(e) => {
                        setMessage(e.target.value);
                      }}
                      required
                    ></input>
                    <button className="msg_send_btn">
                      <SendIcon style={{ color: "#fff" }} />
                    </button>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default connect((state, props) => {
  return {
    user: state?.user,
    isloggedin: state?.isloggedin,
  };
})(Chat);
